function showGoogleMaps(){var o=new google.maps.LatLng(33.214695,-97.128207),e={scrollwheel:!1,navigationControl:!1,mapTypeControl:!1,draggable:!1,zoom:16,streetViewControl:!1,scaleControl:!1,mapTypeId:google.maps.MapTypeId.ROADMAP,center:o};map=new google.maps.Map(document.getElementById("googlemaps"),e),marker=new google.maps.Marker({position:o,map:map,draggable:!1,animation:google.maps.Animation.DROP})}google.maps.event.addDomListener(window,"load",showGoogleMaps);